mapper.select:
		select
		a.content,a.id,a.title,a.picture,a.updated,u.username,u.nickname,a.hits
		,a.status from cms_article a LEFT JOIN cms_user u on a.user_id = u.id

		
			
				a.channel_id =#{channelId}

			
			
				and a.category_id =#{categoryId}

			
			
				and a.hot =#{hot}

			
			
				and a.status =#{status}

			

			
				and a.user_id =#{userId}

			

		

		order by updated desc

		select a.id,a.title,a.picture,a.updated,u.username,u.nickname,a.hits
		,a.status,a.channel_id from cms_article a LEFT JOIN cms_user u on a.user_id = u.id
		
			
				a.channel_id =#{channelId}

			
			
				and a.category_id =#{categoryId}

			
			
				and a.hot =#{hot}

			
			
				and a.status =#{status}

			

			
				and a.user_id =#{userId}

			

		

		order by updated desc

select a.*,u.username,c.`name` ,cat.`name` as catname from cms_article a LEFT JOIN cms_user u on a.user_id = u.id LEFT JOIN cms_channel c on a.channel_id =c.id
LEFT JOIN cms_category cat on a.category_id =cat.id
where a.id =#{id}
